text:
main.campaign section h2:before {
        background-color: rgba(0,129,143,.2);
    }
    .campaign-cta .field-name-field-call-to-action-description+a {
      background-color: #00818f    }
    .campaign-cta .field-name-field-call-to-action-description+a:hover {
      color: white;
      background-color: #006976    }
    .campaign-cta .field-name-field-text-block a {
      color: #00818f    }
    .campaign-content a {
      color: #00818f    }
    div.campaign-content a:hover {
      color: rgba(0,129,143,.5);
    }
    #call-to-action-block a:hover {
      color: white;
    }
    div.campaign-nav-jump-wrapper  a:hover {
      color: rgba(0,129,143,.5);
    }
