}
.panel-default1 {

}

.panel-default1 > .panel-heading1 {
color: #333;
background-color: #f6f9fb;

}
.panel-title1 {
margin-top: 0;
margin-bottom: 0;
font-size: 1.2em;
color: #326e90;
text-decoration: none;
padding:3px 0px;
}
.panel-title1 a{
display:block;
color: #326e90;
text-decoration: underline;
} 

.panel-body {
padding-left:25px; 
}



.panel-title1 a::before {
content: ' ';
display: inline-block;
border-top: 5px solid transparent;
border-bottom: 5px solid transparent;
border-left: 5px solid currentColor;
vertical-align: middle;
margin-right: .7rem;
margin-left:.7rem;
transform: rotate(90deg) translateX(-3px);
transition: transform .2s ease-out;
}
.panel-title1 a.collapsed::before {
transform: rotate(90deg) translateX(-3px);
transform: translateY(-2px);
}

p
{
font-size: 1.0em;
}

.content .columns .col-3-4 ul li, .content .columns .col-3-4 ol li, .content .columns .col-3-4 li ul li
{
font-size: 1.1em;
}

.safe-callout {
margin-top:40px;
background:#dbdfad;
padding:0px 20px 20px 20px;
position:relative;
margin-left:auto;
margin-right:auto;
max-width:90%;
border-color: #999933;
border-style: solid;
border-width: 2px;
}

.safe-blockquote {

background: #f9f9f9;
border-left: 10px solid #ccc;
border-right: 10px solid #ccc;
border-top: 10px solid #ccc;
border-bottom: 10px solid #ccc;
margin: 1.5em 10px;
padding: 0.5em 10px;
}

.safe-callout .safe-callout-title {
background:#999933;
color:#fff;
padding:8px;
position:relative;
top:-10px;
width:70%;
margin:auto;
text-align:center;
text-transform:uppercase;
font-weight: 900;
font-size: 1.5em;
}

