/* Covid styling... Put into the snippet */
.addthis_sharing_toolbox {
display:none;
}
.covid-header {
margin-bottom:5px;
line-height:0;
}
.col.col-gray {
}

hr {
border-color:#92AAB8;
border-width:1px;

}

/* screening tool */
.col.col-screening-tool {
font-size:24px;
background:#D1E8E8;
margin-bottom:5px;
}
.col.col-screening-tool p {
margin:0px;
padding:0px;
}
.col.col-screening-tool .col-2-3 p {
margin:0px;
padding:0 0 0 0;
position:relative;
top:5px;
}
.col.col-screening-tool .col-1-3 p {
float:none;
display:block;
width:100%;
}
.col.col-screening-tool .col-2-3 {
padding:15px 0px 10px 20px;
}
.col.col-screening-tool .col-1-3 {
padding:10px 20px 10px 0px;
text-align:right;
}

.col.col-screening-tool a {
border-radius:10px;
color:#fff;
background: #21A749;
text-decoration:none;
padding:10px;
width:100%;
display:block;
font-weight:bold;
text-align:center;
}


/* Health links info */
.col-health-links {
padding:20px;
font-size:24px;
margin-bottom:5px;
background:#E7E7E8;
}
.col-health-links p:last-child {
padding-bottom:0px;
}

/* search form */
.covid19-form > .col {
margin-bottom:30px;
margin-bottom: 30px;
margin-top: 10px; 
border: 1px solid #92AAB8;
}
.covid19-form .col.col-1-3 {
padding:10px 20px 10px 0;
}
.covid19-form .col.col-2-3 {
padding:0px;
}

/* Cards */
.col.borders {
clear:both;
}



.col.borders .col {
/* padding:0 0 10px 10px; */
padding-bottom: 10px;
}

.col.borders .col.col-1-2 {
/* padding:0 0 10px 10px; */
padding-bottom: 14px;
}

.col.borders .col:first-of-type, .col.borders .col-1-2:first-of-type, .col.borders .col-1-3:first-of-type, .col.borders .col-1-4:first-of-type {
padding-left:0px;
}

.col.borders .card {
display:block;
border: 1px solid #92AAB8;
border-radius: 10px;
padding:20px;
min-height:400px;
font-size:20px;
text-align:center;
}
.col.borders .card:hover {
text-decoration:none;
}

.col.borders .card span {
display:block;
}
.col.borders .card h1 {
font-size:24px;
color:#295975;
font-weight:bold;
min-height:100px;
}





.col.borders .card h1.one-line {
padding-top:30px;
}
.col.borders .card h1.two-line {
padding-top:20px;
}
.col.borders .card h1.three-line {
padding-top:10px;
}
.col.borders .card h1.four-line {
padding-top:0px;
}

.col.borders .card img {
margin-bottom:10px;
}



.covid19-form input[type="search"]{
width:100%;
border:0;
display:inline-block;
margin:0;
font-size:24px;
padding:5px 20px 0px 20px;
height:auto;
color:#58595B;

}
.covid19-form input[type="submit"]{
height:auto;

border:0;
border-radius:10px;
color:#fff;
background: #21A749;
text-decoration:none;
/* padding:10px;*/
width:100%;
display:inline-block;
font-weight:bold;
text-align:center;
margin:0px;
font-size:24px;

}


/* Resources */
.col.col-resources {
margin-top:10px;
border-top: 1px solid #92AAB8;
clear:both;
}
.col.col-resources h2 {
padding:10px 20px;
}


@media screen and (max-width: 767px) {
.col.borders .card {
min-height:auto;
}
.col-screening-tool .col.col-1-3 {
padding-left:20px;
}

.covid19-form .col.col-1-3{
padding-left:20px;
}
.col.borders .col {
padding-left:0px;
}
}

#business-button {
padding-top: 10px;
padding-right: 10px;
padding-bottom: 10px;
padding-left: 10px;
