border-style: solid;
border-color: #326e90;
border-width: 1px;
text-align: center;
font-size: 0.9em;
}

.covid-badge span {
font-weight:bold;
display:block;
min-height:60px;
}
.covid-badge h1 {
margin:0px;
padding:10px 0px 10px 0px;
font-size:36px;
color:#000000;
font-weight:bold;
}

.prs-top-img {
overflow: auto;
}

.prs-img1 {
float: left;
padding-right: 10px;
}

#test-button {
background-color:#e3e6c7;
}

#total-button {
background-color:#b4d9e9; 
}
#active-button {
background-color:#f8d1ca; 
}

#recovered-button {
background-color:#cee8e7;
}

#deaths-button {
background-color:#eadece;
} 

.col-2-4 {
width:46%;
}


