} 

#rha-response {

background-color: #326e90;
padding-top: 15px;
padding-bottom: 5px;
padding-left: 25px;
}



/* Restart MB colours */
.col.borders .card.restartmb h1 {
color:#0E2659;
}
.col.borders .card.restartmb span {
color:#000000;
} 



/* Covid-19 Updates Service Boxes */
.col.boxed .card {
display: table;
color:#fff;
padding: 0px 20px;
width:80%;
height: 140px;
font-size: 20px;
text-align: center;
margin: auto;
}

.col.boxed.boxed-restart .card {
background: #39b54a;

}

.col.boxed.boxed-testing .card {
background: #004185;

}

.col.boxed.boxed-covid .card {
background: #336e8f;

}
.col.boxed.boxed-protect .card {
background: #335973;
}


.col.boxed .card span {
vertical-align:middle;
display:table-cell;
height:80px;
text-align: center;
width: 100%;
}


.col.boxed .col {
text-align:center;
}

.col.boxed .col:nth-child(3n+1) {
margin: unset;
}
.col.boxed .col:nth-child(3n+1) .card {
margin: unset;
}

.col.boxed .col:nth-child(3n+2){
margin:auto;
text-align:center;
}
.col.boxed .col:nth-child(3n+3) .card {
float:right;
}


@media screen and (max-width: 767px) {
.col.boxed .col {
width:100%;
padding-left:0px;
padding-right:0px;
}
.col.boxed .col .card {
width:100%;
text-align:center;
padding-left:20px;
padding-right:20px;
}
.col.boxed .col:nth-child(3n+2){
margin:unset;
text-align:initial;
}
.col.boxed .col:nth-child(3n+3) .card {
float:none;
}
.col.hidden-mobile {
display:none;
}
}


.col-inside-3 ul li {
padding: 10px 10px 6px;
font-size: 1.1em;
}

.col-inside-3 p {
font-size: 1.0em;
}

.col-inside-3 h2
{
font-weight: 700;
}

