/* Fonction pour créer le tableau selon les données reçues */
function generateHtmlTable(data, nomTableau) {
    var html = '<div class="tableauOverflow"><table id="csv-centre-depistage-tableau" class="table table-condensed table-hover table-striped">';
    html += '<thead><tr>';
    html += '<th onclick="sortTable(0)" >Region<span class="fleches"><span></span><span></span></span></th>';
    html += '<th onclick="sortTable(1)"  style="text-align:right;">Testing centre<span class="fleches"><span></span><span></span></span></th>';
    html += '<th style="text-align:right;">Address</th>';
    html += '<th onclick="sortTable(3)"  style="text-align:right;">Wait time<span class="fleches"><span></span><span></span></span></th>';
   html += '<th style="text-align:right;">Additional information</th>';
    html += '</tr></thead>';
    html += '<tbody>';
    /* S'il n'y a pas de données, on ne fait pas le tableau*/
    if(typeof(data[0]) === 'undefined') {
      return null;
    } else {
      //let linkTpl = '<a href="%href" target="_blank" class="region-link">%link&nbsp;<img class="linkExtImg" src="/typo3conf/ext/gabarit_pgu/Resources/Public/Images/Templates/lien-externe.svg" alt="Cet hyperlien s\'ouvrira dans une nouvelle fenêtre."></a>'
      
      /*Sinon, on passe chacune des rangées du CSV*/
      $.each(data, function( index, row ) {
        titre="";
        //if(index >= 10){
            html += '<tr>';
            $.each(row, function( index, colData ) {
              if (index < offset) return
                index = index - offset
              /* Création de la cellule de données */
              if(colData != ""){
                 if (index > 0) {
                
                    html += '<td style="text-align:right;">';
                    if (index == 2) {
                      arr = colData.split(',')
                      len = arr.length-1
                      arr[len] = arr[len].replace(/ /g,'&nbsp;')
                      colData = arr.join(',')
                    }else{
                    
                      if (index == 1) {titre = colData.trim();}
                    }
                
                    if(colData=="En cours " || colData=="En cours"){
                      colData ="Not available";
                    }
                   if(colData=="Actuellement fermée"){
                     colData ="Closed";
                   }
                
                    html += colData.replace(/ /g, " "); 
                
                  } else {
                    
                   /* html += '<td>'; 
                    url = urls[colData.split('.')[0]];
                    html += linkTpl.replace('%href', url).replace('%link', colData)*/
                     html += '<td>';
                     html += colData.replace(/ /g, " "); 
                    
                  }
                  html += '</td>';
                  // Après la dernière colonne de la rangée
                  if (index === (row.length - (offset + 1))) {
                    //detailCentre
                    if(detailCentre[titre]){
                      html += '<td><a href="'+detailCentre[titre]+'" class="detail-link" target="_blank">Details about the centre <span class="visuallyHidden">'+titre+'</span> and opening hours <img class="linkExtImg" src="/typo3conf/ext/gabarit_pgu/Resources/Public/Images/Templates/lien-externe.svg" alt="This hyperlink will open in a new window."></a></td>'; 
                    }else{
                      html += '<td></td>'; 
                    }
                    
                  }
              }
            });
        
            html += '</tr>';
        });
        
      html += '</tbody>';
      html += '</table></div>';
      /* Ajout au DOM du tableau créé*/
      $('#' + nomTableau).append(html);
    }
}
  
function sortTable(n) {
  var table, rows, switching, i, x, y, shouldSwitch, dir, switchcount = 0;
  table = document.getElementById("csv-centre-depistage-tableau");
  switching = true;
  // Set the sorting direction to ascending:
  dir = "asc";
  /* Make a loop that will continue until
  no switching has been done: */
  while (switching) {
    // Start by saying: no switching is done:
    switching = false;
    rows = table.rows;
    /* Loop through all table rows (except the
    first, which contains table headers): */
    for (i = 1; i < (rows.length - 1); i++) {
      // Start by saying there should be no switching:
      shouldSwitch = false;
      /* Get the two elements you want to compare,
      one from current row and one from the next: */
      x = rows[i].getElementsByTagName("TD")[n];
      y = rows[i + 1].getElementsByTagName("TD")[n];
      /* Check if the two rows should switch place,
      based on the direction, asc or desc: */
      if (dir == "asc") {
        if (x.innerHTML.toLowerCase() > y.innerHTML.toLowerCase()) {
          // If so, mark as a switch and break the loop:
          shouldSwitch = true;
          break;
        }
      } else if (dir == "desc") {
        if (x.innerHTML.toLowerCase() < y.innerHTML.toLowerCase()) {
          // If so, mark as a switch and break the loop:
          shouldSwitch = true;
          break;
        }
      }
    }
    if (shouldSwitch) {
      /* If a switch has been marked, make the switch
      and mark that a switch has been done: */
      rows[i].parentNode.insertBefore(rows[i + 1], rows[i]);
      switching = true;
      // Each time a switch is done, increase this count by 1:
      switchcount ++;
    } else {
      /* If no switching has been done AND the direction is "asc",
      set the direction to "desc" and run the while loop again. */
      if (switchcount == 0 && dir == "asc") {
        dir = "desc";
        switching = true;
      }
    }
  }
}  
  
  
