var submitAmount = 0;
    window.addEventListener("DOMContentLoaded", function () {
        $('form.powermail_form').submit(function (event, skipRecaptcha) {
            if (skipRecaptcha) {
                return true;
            }
            if (typeof grecaptcha == 'object') {
                if (!grecaptcha.getResponse()) {
                    event.preventDefault();
                    grecaptcha.execute();
                }
            } else {
                event.preventDefault();
            }
        });
        $('form.powermail_form .powermail_submit').mousedown(function (event) {
            var field = this;
            if (submitAmount === 0) {
                submitAmount++;
                field.disabled = true;
                setTimeout(function enable() {
                    submitAmount = 0;
                    field.disabled = false;
                }, 6000);
                $('form.powermail_form').trigger('submit');
            }
        });
    });
    onCompleted = function () {
        $('form.powermail_form .powermail_submit').prop("disabled", true);
        setTimeout(function enable() {
            submitAmount = 0;
            $('form.powermail_form .powermail_submit').prop("disabled", false);
        }, 2000);
        $('form.powermail_form').trigger('submit', [true]);
    }

